sys_script_client.script:
// 	ScriptLoader.getScripts('x_545800_qa_manage.Test');
function onLoad() {
    //Type appropriate comment here, and begin script below
    if (g_form.isNewRecord() && (!g_form.getValue('u_date') || !g_form.getValue('u_user'))) {
        var alertUtil = new GlideAjax('x_545800_alert.AlertUtil');
        alertUtil.addParam('sysparm_name', 'getManager');
        alertUtil.addParam('sysparm_userID', g_user.userID);
        alertUtil.getXML(function(response) {
            var resp = response.responseXML.documentElement.getAttribute("answer").split(';');
            g_form.setValue('u_date', resp[0]);
            g_form.setValue('u_user', resp[1]);
        });
    }
}
