sys_script_include.script:
var AlertUtil = Class.create();
AlertUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    today: function() {
        return new GlideDate().getByFormat('yyyy-MM-dd') + ' 23:23:59;';
    },
    getManager: function() {
        return new GlideDate().getByFormat('yyyy-MM-dd') + ' 23:23:59;' + AlertUtil.prototype.getManagerS(this.getParameter('sysparm_userID'));
    },
    getManagerS: function(userId) {
        var sysUser = new GlideRecord('sys_user');
        sysUser.addActiveQuery();
        sysUser.addQuery('sys_id', '=', userId);
        sysUser.query();
        if (sysUser.next()) {
            return sysUser.manager || userId;
        }
        return userId;
    },
    createAlert: function() {
        AlertUtil.prototype.createAlertServerSide(this.getParameter('sysparm_uDate'), this.getParameter('sysparm_uDescription'), this.getParameter('sysparm_uUser'), this.getParameter('sysparm_uRepoter'));
    },
    createAlertServerSide: function(u_date, u_description, u_user, repoter) {
        var alert = new GlideRecord('x_545800_alert_alert');
        if (!alert.canCreate()) {
            return;
        }
        alert.initialize();
        alert.u_state = '1';
        alert.u_date = new GlideDateTime(u_date);
        alert.u_description = u_description;
        alert.u_user = u_user;
        alert.u_repoter = repoter || u_user;
        alert.insert();
    },
    getReturnURL: function() {
        var dMap = GlideCascadeFromDelete.getCascadeTables('x_545800_alert_alert', this.getParameter('sysparm_obj_id'), this.getParameter('sysparm_nameofstack'));
        return dMap.remove("return_url");
    },
    deleteAlert: function() {
        AlertUtil.prototype.deleteAlertServerSide(this.getParameter('sysparm_obj_id'));
    },
    deleteAlertServerSide: function(sysId) {
        var alert = new GlideRecord('x_545800_alert_alert');
        if (!alert.canDelete()) {
            return;
        }
        alert.initialize();
        if (alert.get(sysId)) {
            alert.deleteRecord();
        }
    },
    type: 'AlertUtil'
});
AlertUtil.prototype.test = function() {
    return 'call success';
};
